bash -c 'set -euo pipefail; read -rp "GitHub username: " GITHUB_USERNAME; read -rsp "GitHub token (PAT, input hidden): " GITHUB_TOKEN; echo; REPO_URL="https://${GITHUB_USERNAME}:${GITHUB_TOKEN}@github.com/vladzaharia/dangerprep.git"; if [ ! -d "/dangerprep/.git" ]; then sudo git clone "$REPO_URL" /dangerprep; fi; git config --global --add safe.directory /dangerprep; sudo chmod -R 777 /dangerprep; cd /dangerprep; git remote set-url origin https://github.com/vladzaharia/dangerprep.git || true; bash lib/gum/download.sh; bash lib/just/download.sh; sudo scripts/setup/setup-dangerprep.sh'